    const [exercise, setExercise] = useState("");

    useEffect(() => {
        setShowPopup(true);

        const url = `https://wger.de/api/v2/exercise/`;

        fetch(url, {
            headers: {},
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`Error: ${response.status}`);
                }
                return response.json();
            })
            .then((data) => {
                console.log(data);

                setExercise(data);
            })
