import React, { useState } from "react";
import { Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Offcanvas from "react-bootstrap/Offcanvas";
import logo from "../img/logo.png";
import whiteHamburgerIcon from "../img/menu-icon.png";

const Header = () => {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    const closeMenu = () => {
        setShowMenu(false);
    };

    return (
        <>
            <div className="header position-absolute w-100 mt-4">
                <Navbar bg="" expand="lg" fluid>
                    <div className="row">
                        <div className="col-6 d-flex align-items-center">
                            <Link to="/">
                                <img className="logo" src={logo} alt="Logo" />
                            </Link>
                        </div>
                        <div className="col-6 d-flex justify-content-end">
                            <Navbar.Toggle
                                aria-controls="basic-navbar-nav"
                                onClick={toggleMenu}
                                className={showMenu ? "navbar-toggler white" : "navbar-toggler"}
                            >
                                <img src={whiteHamburgerIcon} alt="Menu" className="white-hamburger-icon" />
                            </Navbar.Toggle>
                        </div>
                    </div>
                    <Offcanvas show={showMenu} onHide={closeMenu} placement="end">
                        <Offcanvas.Header closeButton>
                            <Offcanvas.Title>Menu</Offcanvas.Title>
                        </Offcanvas.Header>
                        <Offcanvas.Body>
                            <Nav className="flex-column">
                                <Link className="nav-link" to="/" onClick={closeMenu}>
                                    Home
                                </Link>
                                <Link className="nav-link" to="/timer" onClick={closeMenu}>
                                    Timer
                                </Link>
                            </Nav>
                        </Offcanvas.Body>
                    </Offcanvas>
                </Navbar>
            </div>
        </>
    );
};

export default Header;
