import React, { useState, useEffect } from "react";
import { Modal, Button } from "react-bootstrap";

const Popup = ({ title, message, onClose }) => {
    const [showPopup, setShowPopup] = useState(true);
    const [exercise, setExercise] = useState("");

    useEffect(() => {
        setShowPopup(true);

        const exerciseUrl = `https://wger.de/api/v2/exercise/?language=2`;
        const imageBaseUrl = `https://wger.de/api/v2/exerciseimage/?is_main=True`;

        const fetchExerciseData = async () => {
            try {
                const exerciseResponse = await fetch(exerciseUrl);
                if (!exerciseResponse.ok) {
                    throw new Error(`Exercise data request failed: ${exerciseResponse.status}`);
                }
                const exerciseData = await exerciseResponse.json();

                const randomExercise = getRandomExerciseData(exerciseData.results);

                if (randomExercise) {
                    const exerciseImageResponse = await fetch(`${imageBaseUrl}&exercise=${randomExercise.id}`);
                    if (!exerciseImageResponse.ok) {
                        throw new Error(`Exercise image request failed: ${exerciseImageResponse.status}`);
                    }
                    const exerciseImage = await exerciseImageResponse.json();

                    // Update exercise state with both exercise data and image
                    setExercise({ ...randomExercise, image: exerciseImage.results[0].image });
                }
            } catch (error) {
                console.error("Request failed:", error);
            }
        };

        fetchExerciseData();
    }, []);

    const getRandomExerciseData = (exerciseResults) => {
        if (exerciseResults && exerciseResults.length > 0) {
            const randomIndex = Math.floor(Math.random() * exerciseResults.length);
            return exerciseResults[randomIndex];
        }
        return null;
    };
    return (
        <Modal show={showPopup} onHide={handleClose} centered>
            <Modal.Header closeButton>
                <Modal.Title>{exercise.name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <img src={exercise.image} alt="Exercise" />
                {exercise.description}
            </Modal.Body>
            <Modal.Footer></Modal.Footer>
        </Modal>
    );
};

export default Popup;
