import React, { useState, useEffect } from "react";
import { Modal, Button } from "react-bootstrap";
import data from "../data/exercises";

const Popup = ({ onClose }) => {
    const [showPopup, setShowPopup] = useState(true);

    const exercises = data;
    const randomExerciseIndex = Math.floor(Math.random() * exercises.length);
    const randomExercise = exercises[randomExerciseIndex];

    const handleClose = () => {
        setShowPopup(false);
        onClose();
    };

    return (
        <Modal show={showPopup} onHide={handleClose} centered>
            <Modal.Header closeButton className="modal-header">
                <button className="close red-close-button" onClick={handleClose}>
                    <span>&times;</span>
                </button>
                <Modal.Title>{randomExercise.name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>{randomExercise.desc}</Modal.Body>
        </Modal>
    );
};

export default Popup;
