import React, { useState } from "react";
import { Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Offcanvas from "react-bootstrap/Offcanvas";
import logo from "../img/logo.png";

const Header = () => {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    const closeMenu = () => {
        setShowMenu(false);
    };

    return (
        <>
            <div className="header position-absolute w-100 d-flex justify-content-center mt-4">
                <Navbar bg="" expand="lg">
                    <Container>
                        <Link to="/">
                            <img className="logo" src={logo} alt="Logo" />
                        </Link>

                        <Navbar.Toggle
                            aria-controls="basic-navbar-nav"
                            onClick={toggleMenu}
                            className="text-white" // Add the text-white class to make the hamburger icon white
                        />

                        <Offcanvas show={showMenu} onHide={closeMenu} placement="end">
                            <Offcanvas.Header closeButton>
                                <Offcanvas.Title>Menu</Offcanvas.Title>
                            </Offcanvas.Header>
                            <Offcanvas.Body>
                                <Nav className="flex-column">
                                    <Link className="nav-link" to="/" onClick={closeMenu}>
                                        Home
                                    </Link>
                                    <Link className="nav-link" to="/timer" onClick={closeMenu}>
                                        Timer
                                    </Link>
                                </Nav>
                            </Offcanvas.Body>
                        </Offcanvas>
                    </Container>
                </Navbar>
            </div>
        </>
    );
};

export default Header;
