import React, { useState } from "react";
import { Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Offcanvas from "react-bootstrap/Offcanvas";
import logo from "../img/logo.png";
import whiteHamburgerIcon from "../img/menu-icon.png";

const Header = () => {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    const closeMenu = () => {
        setShowMenu(false);
    };

    return (
        <>
            <div className="header position-absolute w-100 mt-4">
                <Navbar bg="" expand="lg">
                    <Container className="justify-content-center">
                        <div className="row">
                            <div className="col-12 d-flex justify-content-center pb-4">
                                <Link to="/">
                                    <img className="logo" src={logo} alt="Logo" />
                                </Link>
                            </div>
                            <div className="col-12 d-flex justify-content-center">
                                <Navbar.Toggle
                                    aria-controls="basic-navbar-nav"
                                    onClick={toggleMenu}
                                    className={showMenu ? "navbar-toggler white" : "navbar-toggler"}
                                >
                                    <div className="menu-container">
                                        <span className="text-white menu-text">Menu</span>
                                    </div>
                                </Navbar.Toggle>
                            </div>
                        </div>
                        <Offcanvas show={showMenu} onHide={closeMenu} placement="top">
                            <Offcanvas.Header className="mx-auto text-white" closeButton>
                                <Offcanvas.Title></Offcanvas.Title>
                            </Offcanvas.Header>
                            <Offcanvas.Body>
                                <Nav className="flex-column">
                                    <Link className="nav-link" to="/" onClick={closeMenu}>
                                        Home
                                    </Link>
                                    <Link className="nav-link" to="/timer" onClick={closeMenu}>
                                        Timer
                                    </Link>
                                </Nav>
                            </Offcanvas.Body>
                        </Offcanvas>
                    </Container>
                </Navbar>
            </div>
        </>
    );
};

export default Header;
